import React, { useState, useEffect, useRef, useMemo } from "react";
import { getAIMessage } from "../api/api";
import { marked } from "marked";
import "./ChatWindow.css";

// Memoized Message component
const Message = React.memo(({ role, content, isLoading }) => {
  return (
    <div className={`message-wrapper ${role}`}>
      <div className="message-bubble">
        {isLoading ? (
          <div className="loading-bar">Typing...</div>
        ) : (
          <div
            dangerouslySetInnerHTML={{
              __html: marked(content).replace(/<p>|<\/p>/g, ""),
            }}
          />
        )}
      </div>
    </div>
  );
});

function ChatWindow() {
  const defaultMessage = [
    {
      role: "assistant",
      content: "Hi, how can I help you today?",
    },
  ];

  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (input) => {
    if (input.trim() !== "") {
      const userMessage = { role: "user", content: input };
      setMessages((prev) => [...prev, userMessage]);
      setInput("");

      // Add temporary loading message
      const loadingMessage = { role: "assistant", content: "", isLoading: true };
      setMessages((prev) => [...prev, loadingMessage]);

      // Fetch real AI response
      const aiMessage = await getAIMessage(input);

      // Replace loading with real response
      setMessages((prev) => {
        const updated = [...prev];
        updated[updated.length - 1] = aiMessage; // Replace last item
        return updated;
      });
    }
  };

  const recentMessages = useMemo(() => {
    return messages.slice(-4);
  }, [messages]);

  return (
    <div className="chat-container">
      <div className="chat-messages">
        {recentMessages.map((message, index) => (
          <Message
            key={index}
            role={message.role}
            content={message.content}
            isLoading={message.isLoading}
          />
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-area">
        <textarea
          className="chat-input"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message..."
          onKeyDown={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              handleSend(input);
              e.preventDefault();
            }
          }}
          rows="2"
        />
        <button className="chat-send-button" onClick={() => handleSend(input)}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
